import React from 'react';
import InputComp from './InputComp';

let TrueName= "Saro";
let TruePassword= "Saro";

class LogIn extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            Login: "",
            Password: ""
        }
    }

    onSubmit =(e)=> {
        e.preventDefault();
        if(TrueName === this.state.Login && TruePassword === this.state.Password){
            this.props.nextStep(true)
        }
        else{
            console.log(this.state.Login);
            console.log(this.state.Password);
            alert("wrong login or password");
        }
    };

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
        console.log({[e.target.name]: e.target.value })
    };


    render(){
        return(
            <form className="formWrapp" onSubmit={this.onSubmit}>
                <div className="row align-center">
                    <div className="column small-9">
                        <div className="textCenter">
                            <h1>
                                Մուտք
                            </h1>
                        </div>
                    </div>
                   
                    <div className="column small-9">
                        <InputComp
                            otherFormat={null}
                            handleChange={this.handleChange}
                            InputTitle={"Login"}
                            InputType={"text"}
                            InputName={"Login"}
                            DefoltVal={this.state.Login}
                        />
                    </div>
                    <div className="column small-9">
                        <InputComp
                            otherFormat={null}
                            handleChange={this.handleChange}
                            InputTitle={"Password"}
                            InputType={"password"}
                            InputName={"Password"}
                            DefoltVal={this.state.Password}
                        />
                    </div>
                    <div className="column small-9 flex-container align-center">
                        <button type="submit" className="btn orange" onClick={this.continue}>
                            Մտնել
                        </button>
                    </div>
                </div>
            </form>
        )
    }
};

export default LogIn;
